import './Card.css'

export const Card = ({ img, desconto, precoOrig, price }) => {

    const formatNumber = (number) => number.toLocaleString('pt-BR', { minimumFractionDigits: 2, maximumFractionDigits: 2 });
    const cardStyle = {
        backgroundImage: `url(${img})`,
    };

    return (
        <div className="card" style={cardStyle}>
            <div className="container-price">
                <span className="promo">{desconto}</span>
                <div className="group-price">
                    <span className="original-price">{formatNumber(precoOrig)}</span>
                    <span className="price">{formatNumber(price)}</span>
                </div>
            </div>
        </div>
    )
}
