import React, { useState, useEffect, useMemo, useCallback } from 'react';
import Card from './Card';
import './styles.css';

const gamesData = [
  { id: 1, title: "Game 1", imageUrl: "/path/to/image1.jpg", price: 29.99, promoPrice: 19.99 },
  { id: 2, title: "Game 2", imageUrl: "/path/to/image2.jpg", price: 49.99, promoPrice: 29.99 },
  // Adicione mais jogos aqui
];

const App = () => {
  const [games, setGames] = useState([]);
  const [filter, setFilter] = useState('');

  useEffect(() => {
    // Simula a busca de dados
    setGames(gamesData);
  }, []);

  const filteredGames = useMemo(() => {
    return games.filter(game => game.title.toLowerCase().includes(filter.toLowerCase()));
  }, [games, filter]);

  const handleFilterChange = useCallback((event) => {
    setFilter(event.target.value);
  }, []);

  return (
    <div>
      <header>
        <div className="website-title">
          <h1>Game Store</h1>
        </div>
        <div className="navigation">
          <input 
            type="text" 
            placeholder="Search games..." 
            value={filter} 
            onChange={handleFilterChange} 
          />
        </div>
      </header>
      <main>
        {filteredGames.map(game => (
          <Card key={game.id} game={game} />
        ))}
      </main>
    </div>
  );
};

export default App;
